
const fs 		= require('fs'),
	  marked 	= require('marked'),
	  toml 		= require('toml');


module.exports = {

	file : ( file, cb ) => {
		return (cb)
			? fs.readFile(file, 'utf8', cb)
			: fs.readFileSync(file, 'utf8');
	},

	data : ( data ) => {
		return toml.parse(data);
	},

	content : ( content ) => {
		return marked(content);
	}

};