
module.exports = ( files ) => {
	
	// loop through _.glob of files
	// return new array of paths only
	const dirs = ((files) => {

		let dirs = [];

		for ( let f = 0; f < files.length; f++ )
		{
			let file = files[f].replace(/\.\/.+?\//, '{root}/').split(/\/[^\/]*?\.(md|html)/)[0];

			dirs.push( file );
		}

		return dirs;

	})(files);


	// filter out duplicates in array
	const unique = [ ...new Set(dirs) ];


	// sort array from shallowest dirs to deepest
	const sorted = unique.sort(function(a, b) {

		return a.split('/').length - b.split('/').length;

	});


	return sorted;

};