
class Stopwatch {


    constructor()
    {

        this.timers = {};

    }


    start( type )
    {
        this.timers[type] = {
            start : new Date().getTime() / 1000
        };
    }


    stop( type )
    {
        this.timers[type].stop = new Date().getTime() / 1000;
    }


    get( type )
    {
        return `${ (this.timers[type].stop - this.timers[type].start).toFixed(4) }s`;
    }


    log( type )
    {
        return console.log(`Timer : ${type} took ${this.get(type)}`);
    }


}


module.exports = new Stopwatch();