
const colour = require('colour');


module.exports = (err, settings) => {

	let parts = err.path.replace(/\/[^\/]*?\.(md|html)/, '').split('/');

	parts.pop();

	let path = parts.join('/');


	switch ( err.syscall )
	{

		case 'mkdir' : console.error(colour.red(`!!!  Chief not find index page at ${path.replace(settings.compiled, settings.content)}  !!!`));
		break;

		case 'open' : console.error(colour.red(`!!!  Chief not find directory at ${path}  !!!`));
		break;

	}

	return;

	

};