
module.exports = ( files, root, ext ) => {
	
	// loop through _.glob of files
	// return new array of paths only
	const dirs = ((files) => {

		let dirs = [];

		for ( let f = 0; f < files.length; f++ )
		{
			let file = files[f].replace('{root}', root).replace('{ext}', `.${ext}`);

			dirs.push( file );
		}

		return dirs;

	})(files);


	return ( dirs.length > 0 ) ? dirs : false;

};