
const _getTemplate = ( data, dirs, templates, settings ) => {

	if ( dirs.length > 0 && dirs[0] !== settings.index )
	{

		const dir 		= dirs[0],
			  current 	= data[dir];

		const template = ( current.hasOwnProperty('config') )

							? ( current.config.hasOwnProperty('template') ) 

								? current.config.template 

								: false

							: false;

		dirs.shift();

		templates.unshift(template);

		return _getTemplate(current.pages, dirs, templates);

	}
	else
	{
		return templates;
	}

};


module.exports = _getTemplate;
